import java.io.BufferedInputStream;
import java.io.File;
import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.FileOutputStream;
import java.io.FileWriter;
import java.io.IOException;
import java.io.InputStream;
import java.io.StringReader;
import java.rmi.RemoteException;
import java.util.HashSet;
import java.util.Set;
import java.util.zip.ZipEntry;
import java.util.zip.ZipOutputStream;
import javax.xml.namespace.QName;

/*
	 * Writes a set of files to a zip file, and returns the name of
	 * the zip file.
	 */
	private String writeZipFile(Set tempFiles) throws IOException {
		System.out.println("entering writeZipFile with: " + tempFiles.size() + " to write");
		ZipOutputStream zos = null;
		byte[] buffer = new byte[1024];
		int bytesRead;
		File zipFile = File.createTempFile("temp", ".zip");			

		try {

			zos = new ZipOutputStream(new FileOutputStream(zipFile));
			int i = 1;
			for (File file : tempFiles) {
				ZipEntry entry = new ZipEntry(file.getName());
				zos.putNextEntry(entry);
				BufferedInputStream bis = null;
				System.out.println("writing zip entry for: " + i + " out of " + tempFiles.size());
				System.out.println("writing zip entry for: " + file.getName());
				i++;
				try {
					bis = new BufferedInputStream(new FileInputStream(file));
					while ((bytesRead = bis.read(buffer)) != -1) {
						zos.write(buffer, 0, bytesRead);
					}
				} finally {
					if (null != bis) bis.close();
				}
				System.out.println("wrote zip entry for: " + file.getName());				

			}
		} catch (FileNotFoundException e) {
			System.out.println("Error creating zip file: " + zipFile.getName());
			throw e;
		} catch (IOException e) {
			System.out.println("Error writing entry to zip file: " + zipFile.getName());
			throw e;
		} finally {
			if (null != zos) try {zos.close();} catch (IOException e) { }
		}
		return zipFile.getCanonicalPath();
	}



